mapper.select:
SELECT COUNT(1) > 0 FROM sys_user_organization
WHERE user_id = #{userId} AND organization_id = #{organizationId}
LIMIT 1

mapper.update:
UPDATE sys_user_organization SET is_primary = 0 WHERE user_id = #{userId};
UPDATE sys_user_organization SET is_primary = 1, update_time = NOW()
WHERE user_id = #{userId} AND organization_id = #{organizationId};
